import { StQuizGeneral } from './QuizGeneralStyle';
export default function App() {
	 const questions = [
		{
			question: <img src="https://d.newsweek.com/en/full/1529160/elephant-appreciation-day-2019.jpg" alt="error"></img>,
			options: ["Jupiter","Saturn","Neptune","Mercury"],
			correct_option: "Jupiter"
		},
		{
			question: <img src="https://hddesktopwallpapers.in/wp-content/uploads/2015/09/cat-eyes-cute.jpg" alt="e"></img> ,
			options: ["Chand Minar","Taj Mahal","Stadium"],
			correct_option: "Taj Mahal"
		},
		{
			question: <img src="https://castlehorsefeeds.com/wp-content/uploads/2019/03/spring-horses.jpg" alt="r"></img>,
			options: ["Alligator","Crocodile","Baboon","Hippo"],
			correct_option: "Hippo"
		},
		{
			question:<img src="https://castlehorsefeeds.com/wp-content/uploads/2019/03/spring-horses.jpg" alt="r"></img>,
			options: ["The African elephant","The blue whale","The sperm whale","The giant squid"],
			correct_option: "The blue whale"
		},
		{
			question: <img src="https://www.hdnicewallpapers.com/Walls/Big/Lion/Lion_in_Gir_Forest_National_Park_India.jpg" alt="o"></img>,
			options: ["The bat","The flying squirrel","The bald eagle","The colugo"],
			correct_option: "The bat"
		}
	]
	// 	{
	// 		questionText: <img src="https://d.newsweek.com/en/full/1529160/elephant-appreciation-day-2019.jpg" alt="error"></img>,
	// 		answerOptions: [
	// 			{ answerText: 'cat', isCorrect: false },
	// 			{ answerText: 'dog', isCorrect: false },
	// 			{ answerText: 'elephant', isCorrect: true },
	// 			{ answerText: 'lion', isCorrect: false },
	// 		],
	// 	},
	// 	{
	// 		questionText: <img src="https://hddesktopwallpapers.in/wp-content/uploads/2015/09/cat-eyes-cute.jpg" alt="e"></img>,
	// 		answerOptions: [
	// 			{ answerText: 'lion', isCorrect: false },
	// 			{ answerText: 'cat', isCorrect: true },
	// 			{ answerText: 'horse', isCorrect: false },
	// 			{ answerText: 'dog', isCorrect: false },
	// 		],
	// 	},
	// 	{
	// 		questionText: <img src="https://castlehorsefeeds.com/wp-content/uploads/2019/03/spring-horses.jpg" alt="r"></img>,
	// 		answerOptions: [
	// 			{ answerText: 'horse', isCorrect: true },
	// 			{ answerText: 'dog', isCorrect: false },
	// 			{ answerText: 'cat', isCorrect: false },
	// 			{ answerText: 'lion', isCorrect: false },
	// 		],
	// 	},
	// 	{
	// 		questionText: <img src="https://www.hdnicewallpapers.com/Walls/Big/Lion/Lion_in_Gir_Forest_National_Park_India.jpg" alt="o"></img>,
	// 		answerOptions: [
	// 			{ answerText: 'cat', isCorrect: false },
	// 			{ answerText: 'horse', isCorrect: false },
	// 			{ answerText: 'dog', isCorrect: false },
	// 			{ answerText: 'lion', isCorrect: true },
	// 		],
	// 	},
	// ];


	// const [currentQuestion, setCurrentQuestion] = useState(0);
	// const [showScore, setShowScore] = useState(false);
	// const [score, setScore] = useState(0);

	// const handleAnswerOptionClick = (isCorrect) => {
	// 	if (isCorrect) {
	// 		setScore(score + 1);
	// 	}

	// 	const nextQuestion = currentQuestion + 1;
	// 	if (nextQuestion < questions.length) {
	// 		setCurrentQuestion(nextQuestion);
	// 	} else {
	// 		setShowScore(true);
	// 	}
	// };
	const Quiz = () => {

		const allQuestions = data;
		const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)
		const [currentOptionSelected, setCurrentOptionSelected] = useState(null);
		const [correctOption, setCorrectOption] = useState(null);
		const [isOptionsDisabled, setIsOptionsDisabled] = useState(false);
		const [score, setScore] = useState(0)
		const [showNextButton, setShowNextButton] = useState(false)
		const [showScoreModal, setShowScoreModal] = useState(false)

		const validateAnswer = (selectedOption) => {
			let correct_option = allQuestions[currentQuestionIndex]['correct_option'];
			setCurrentOptionSelected(selectedOption);
			setCorrectOption(correct_option);
			setIsOptionsDisabled(true);
			if(selectedOption==correct_option){
				// Set Score
				setScore(score+1)
			}
			// Show Next Button
			setShowNextButton(true)
		}
		const handleNext = () => {
			if(currentQuestionIndex== allQuestions.length-1){
				// Last Question
				// Show Score Modal
				setShowScoreModal(true)
			}else{
				setCurrentQuestionIndex(currentQuestionIndex+1);
				setCurrentOptionSelected(null);
				setCorrectOption(null);
				setIsOptionsDisabled(false);
				setShowNextButton(false);
			}
			Animated.timing(progress, {
				toValue: currentQuestionIndex+1,
				duration: 1000,
				useNativeDriver: false
			}).start();
		}
		const restartQuiz = () => {
			setShowScoreModal(false);

			setCurrentQuestionIndex(0);
			setScore(0);

			setCurrentOptionSelected(null);
			setCorrectOption(null);
			setIsOptionsDisabled(false);
			setShowNextButton(false);
			Animated.timing(progress, {
				toValue: 0,
				duration: 1000,
				useNativeDriver: false
			}).start();
		}


		const Quiz = () => {

			const allQuestions = data;
			const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0)
			const [currentOptionSelected, setCurrentOptionSelected] = useState(null);
			const [correctOption, setCorrectOption] = useState(null);
			const [isOptionsDisabled, setIsOptionsDisabled] = useState(false);
			const [score, setScore] = useState(0)
			const [showNextButton, setShowNextButton] = useState(false)
			const [showScoreModal, setShowScoreModal] = useState(false)

			const validateAnswer = (selectedOption) => {
				let correct_option = allQuestions[currentQuestionIndex]['correct_option'];
				setCurrentOptionSelected(selectedOption);
				setCorrectOption(correct_option);
				setIsOptionsDisabled(true);
				if(selectedOption==correct_option){
					// Set Score
					setScore(score+1)
				}
				// Show Next Button
				setShowNextButton(true)
			}
			const handleNext = () => {
				if(currentQuestionIndex== allQuestions.length-1){
					// Last Question
					// Show Score Modal
					setShowScoreModal(true)
				}else{
					setCurrentQuestionIndex(currentQuestionIndex+1);
					setCurrentOptionSelected(null);
					setCorrectOption(null);
					setIsOptionsDisabled(false);
					setShowNextButton(false);
				}
				Animated.timing(progress, {
					toValue: currentQuestionIndex+1,
					duration: 1000,
					useNativeDriver: false
				}).start();
			}
			const restartQuiz = () => {
				setShowScoreModal(false);

				setCurrentQuestionIndex(0);
				setScore(0);

				setCurrentOptionSelected(null);
				setCorrectOption(null);
				setIsOptionsDisabled(false);
				setShowNextButton(false);
				Animated.timing(progress, {
					toValue: 0,
					duration: 1000,
					useNativeDriver: false
				}).start();
			}



			const renderQuestion = () => {
				return (
					<View style={{
						marginVertical: 40
					}}>
						{/* Question Counter */}
						<View style={{
							flexDirection: 'row',
							alignItems: 'flex-end'
						}}>
							<Text style={{color: COLORS.white, fontSize: 20, opacity: 0.6, marginRight: 2}}>{currentQuestionIndex+1}</Text>
							<Text style={{color: COLORS.white, fontSize: 18, opacity: 0.6}}>/ {allQuestions.length}</Text>
						</View>

						{/* Question */}
						<Text style={{
							color: COLORS.white,
							fontSize: 30
						}}>{allQuestions[currentQuestionIndex]?.question}</Text>
					</View>
				)
			}
			const renderOptions = () => {
				return (
					<View>
						{
							allQuestions[currentQuestionIndex]?.options.map(option => (
								<TouchableOpacity 
								onPress={()=> validateAnswer(option)}
								disabled={isOptionsDisabled}
								key={option}
								style={{
									borderWidth: 3, 
									borderColor: option==correctOption 
									? COLORS.success
									: option==currentOptionSelected 
									? COLORS.error 
									: COLORS.secondary+'40',
									backgroundColor: option==correctOption 
									? COLORS.success +'20'
									: option==currentOptionSelected 
									? COLORS.error +'20'
									: COLORS.secondary+'20',
									height: 60, borderRadius: 20,
									flexDirection: 'row',
									alignItems: 'center', justifyContent: 'space-between',
									paddingHorizontal: 20,
									marginVertical: 10
								}}
								>
									<Text style={{fontSize: 20, color: COLORS.white}}>{option}</Text>

									{/* Show Check Or Cross Icon based on correct answer*/}
									{
										option==correctOption ? (
											<View style={{
												width: 30, height: 30, borderRadius: 30/2,
												backgroundColor: COLORS.success,
												justifyContent: 'center', alignItems: 'center'
											}}>
												<MaterialCommunityIcons name="check" style={{
													color: COLORS.white,
													fontSize: 20
												}} />
											</View>
										): option == currentOptionSelected ? (
											<View style={{
												width: 30, height: 30, borderRadius: 30/2,
												backgroundColor: COLORS.error,
												justifyContent: 'center', alignItems: 'center'
											}}>
												<MaterialCommunityIcons name="close" style={{
													color: COLORS.white,
													fontSize: 20
												}} />
											</View>
										) : null
									}

								</TouchableOpacity>
							))
						}
					</View>
				)
			}
			const renderNextButton = () => {
				if(showNextButton){
					return (
						<TouchableOpacity
						onPress={handleNext}
						style={{
							marginTop: 20, width: '100%', backgroundColor: COLORS.accent, padding: 20, borderRadius: 5
						}}>
							<Text style={{fontSize: 20, color: COLORS.white, textAlign: 'center'}}>Next</Text>
						</TouchableOpacity>
					)
				}else{
					return null
				}
			}


			const [progress, setProgress] = useState(new Animated.Value(0));
			const progressAnim = progress.interpolate({
				inputRange: [0, allQuestions.length],
				outputRange: ['0%','100%']
			})
			const renderProgressBar = () => {
				return (
					<View style={{
						width: '100%',
						height: 20,
						borderRadius: 20,
						backgroundColor: '#00000020',

					}}>
						<Animated.View style={[{
							height: 20,
							borderRadius: 20,
							backgroundColor: COLORS.accent
						},{
							width: progressAnim
						}]}>

						</Animated.View>

					</View>
				)
			}


			return (
			   <SafeAreaView style={{
				   flex: 1
			   }}>
				   <StatusBar barStyle="light-content" backgroundColor={COLORS.primary} />
				   <View style={{
					   flex: 1,
					   paddingVertical: 40,
					   paddingHorizontal: 16,
					   backgroundColor: COLORS.background,
					   position:'relative'
				   }}>

					   {/* ProgressBar */}
					   { renderProgressBar() }

					   {/* Question */}
					   {renderQuestion()}

					   {/* Options */}
					   {renderOptions()}

					   {/* Next Button */}
					   {renderNextButton()}

					   {/* Score Modal */}
					   <Modal
					   animationType="slide"
					   transparent={true}
					   visible={showScoreModal}
					   >
						   <View style={{
							   flex: 1,
							   backgroundColor: COLORS.primary,
							   alignItems: 'center',
							   justifyContent: 'center'
						   }}>
							   <View style={{
								   backgroundColor: COLORS.white,
								   width: '90%',
								   borderRadius: 20,
								   padding: 20,
								   alignItems: 'center'
							   }}>
								   <Text style={{fontSize: 30, fontWeight: 'bold'}}>{ score> (allQuestions.length/2) ? 'Congratulations!' : 'Oops!' }</Text>

								   <View style={{
									   flexDirection: 'row',
									   justifyContent: 'flex-start',
									   alignItems: 'center',
									   marginVertical: 20
								   }}>
									   <Text style={{
										   fontSize: 30,
										   color: score> (allQuestions.length/2) ? COLORS.success : COLORS.error
									   }}>{score}</Text>
										<Text style={{
											fontSize: 20, color: COLORS.black
										}}>/ { allQuestions.length }</Text>
								   </View>
								   {/* Retry Quiz button */}
								   <TouchableOpacity
								   onPress={restartQuiz}
								   style={{
									   backgroundColor: COLORS.accent,
									   padding: 20, width: '100%', borderRadius: 20
								   }}>
									   <Text style={{
										   textAlign: 'center', color: COLORS.white, fontSize: 20
									   }}>Retry Quiz</Text>
								   </TouchableOpacity>

							   </View>

						   </View>
					   </Modal>

					   {/* Background Image */}
					   <Image
						source={require('../assets/images/DottedBG.png')}
						style={{
							width: SIZES.width,
							height: 130,
							zIndex: -1,
							position: 'absolute',
							bottom: 0,
							left: 0,
							right: 0,
							opacity: 0.5
						}}
						resizeMode={'contain'}
						/>

				   </View>
			   </SafeAreaView>
			)
		}

}}
